# Define the host and port
HOST, PORT = '0.0.0.0', 80
def template(c, *args, **kwargs):
    a = open(f'templates/{c}', 'r', encoding='utf-8', errors='ignore')
    b = a.read()
    for i in kwargs:
        b = b.replace(f"/*{i}*/unset", f"{kwargs[i]}")
        b = b.replace(f"//*{i}", f"{kwargs[i]}")
    a.close()
    if "nein" not in kwargs:
        b = b.replace("<title>", "<title id=\"title_to_replace\">")
    return b
 
class Server:
    def __init__(self):
        HOST
        PORT
        socket
        self._cached_ = {}
        self.paths = {}
        self.aps = []
        self.static = "static"
        self._a_ = template
        self.server_socket = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self.server_socket.bind((HOST, PORT))
        self.server_socket.listen(5)
        self.builtin = '''<script id="builtin">async function getData(l, _body) {
        const url = l;
        try {
        const response = await fetch(url, {method: "POST",body: _body});
        if (!response.ok) {
            throw new Error(`Response status: ${response.status}`);
        }
        const json = await response.json();
        return json;
        } catch (error) {
        console.error(error.message);
        }
        }</script>
        <script>
        document.getElementById("builtin").innerHTML = "";
        </script>
        <script>'''+js_code+'''</script>
        '''+js_icons+'''
        '''
        threading.Thread(target=self.handle_escape, daemon=True).start()
            
    def parse_headers(self, request):
        headers = {}
        lines = request.split('\r\n')
        for line in lines[1:]:  # Skip the request line
            if line == '':  # Headers end with an empty line
                break
            key, value = line.split(': ', 1)
            headers[key] = value
        return headers

    def get_cookies(self, headers):
        if ("Cookie" in headers):
            cookies = headers.split(": ")[1]
            cookie_dict = {}
            for cookie in cookies.split('; '):
                name, value = cookie.split('=', 1)
                cookie_dict[name] = value
            return cookie_dict
        return {}

    def set_cookie(self, key, value, response_):
        response = (
            "HTTP/1.1 200 OK\r\n"
            "Content-Type: text/html; charset=utf-8\r\n"
            f"Set-Cookie: {key}={value}; Path=/; HttpOnly\r\n"
            "\r\n"
            f"{response_}"
        )
        return response
    def start(self):
        with ThreadPoolExecutor(max_workers=1000) as executor:
            executor.submit(self.background)
        self.background()
    
    def handle_escape(self):
        while True:
            key = getch()
            if (key == b'\x03'):
                print("Ctrl^C Closing.")
                self.server_socket.close()
                exit(0)

    def background(self):
        while True:
            try:
                sock, client_address = self.server_socket.accept()
                threading.Thread(target=asyncio.run, args=(self.validate(sock), )).start()
            except Exception as e:
                print("error: ", e)
                quit(1)
    
    def recog(self, sock, recved, path, c, cookies):
        for i in range(len(self.aps)):
            ap = list(self.paths.values())[i]
            if ("?" in path):
                y = path.split('?')[0]+"[.?]"
                if (y == self.aps[i].split()[1]):
                    pattern = r'[?&]([^=&]+)=([^&]*)'
                    x = re.findall(pattern, path)
                    query_params = {key: value for key, value in x}
                    sock.sendall(self.usual(sock, self.aps[i], **query_params).encode())
                    #sock.sendall(fourofour)
                    return True
            elif (recved == self.aps[i]):
                #TODO - assert not working the website exchange! fix
                self.allow_user(sock, ap, c, cookies)
                return True
        return False
    
    def usual(self, sock, path, *args, **kwargs):
        try:
            l  = self._cached_[path]
            fourofour = l[0]
            header = l[1]
            sock.sendall(header.encode('utf-8'))
        except:
            fourofour = self.paths[path]
        if (kwargs != {}):
            fourofour = fourofour(**kwargs)
        return fourofour
    
