def round_to_nearest_0_or_60(number):
    # Calculate the difference to 0 and 60
    diff_to_0 = abs(number - 0)
    diff_to_30 = abs(number - 30)
    diff_to_60 = abs(number - 60)
    
    # Determine the smallest difference and return the corresponding value
    if diff_to_0 <= diff_to_30 and diff_to_0 <= diff_to_60:
        return 0
    elif diff_to_30 <= diff_to_0 and diff_to_30 <= diff_to_60:
        return 30
    else:
        return 60

